def check_prime(n):
    # if the number is less than or equal to 1, it's not prime
    if n <= 1:
        return False

    # 2 and 3 are prime numbers
    if n <= 3:
        return True

    # check if the number is divisible by 2 or 3
    if n % 2 == 0 or n % 3 == 0:
        return False

    i = 5
    while(i * i <= n):
        # check if the number is divisible by any number up to its square root
        if (n % i == 0 or n % (i + 2) == 0):
            return False
        i += 6

    return True

