function countFrequency(arr) {
  const freqObj = {};

  // Count the frequency of each integer in the array
  for (let num of arr) {
    if (freqObj.hasOwnProperty(num)) {
      freqObj[num]++;
    } else {
      freqObj[num] = 1;
    }
  }

  // Sort the object by frequency in descending order
  const sortedFreqObj = Object.entries(freqObj).sort((a, b) => b[1] - a[1]).reduce((obj, [key, value]) => {
    obj[key] = value;
    return obj;
  }, {});

  return sortedFreqObj;
}

