function calculateStats(arr) {
  if (!Array.isArray(arr)) {
    throw new Error("Input must be an array");
  }

  let sum = 0;
  let min = Number.MAX_VALUE;
  let max = Number.MIN_VALUE;

  for (let i = 0; i < arr.length; i++) {
    const num = arr[i];

    if (typeof num !== "number") {
      throw new Error(`Element at index ${i} is not a number`);
    }

    sum += num;
    min = Math.min(min, num);
    max = Math.max(max, num);
  }

  const avg = sum / arr.length;

  return {
    sum: sum,
    average: avg,
    minimum: min,
    maximum: max,
  };
}

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;

public class StudentAverage {

    public static void main(String[] args) {
        String csvFile = "students.csv";
        String line;
        int totalAge = 0;
        int totalGrade = 0;
        int count = 0;

        try (BufferedReader br = new BufferedReader(new FileReader(csvFile))) {
            while ((line = br.readLine()) != null) {
                String[] studentData = line.split(",");

                if (studentData.length < 3) {
                    System.out.println("Skipping row with missing data: " + line);
                    continue;
                }

                try {
                    int age = Integer.parseInt(studentData[1].trim());
                    int grade = Integer.parseInt(studentData[2].trim());

                    totalAge += age;
                    totalGrade += grade;
                    count++;
                } catch (NumberFormatException e) {
                    System.out.println("Skipping row with invalid data: " + line);
                }
            }

            double averageAge = (double) totalAge / count;
            double averageGrade = (double) totalGrade / count;

            System.out.printf("Average Age: %.2f%n", averageAge);
            System.out.printf("Average Grade: %.2f%n", averageGrade);
        } catch (IOException e) {
            System.err.println("Error reading file: " + e.getMessage());
        }
    }
}

